import React, { useEffect, useState } from 'react';
import { getAllCommentsForMovie } from '../api/commentApi';

interface MovieCommentsProps {
  movieId: string;
}

const MovieComments = ({ movieId }: MovieCommentsProps) => {
  interface Comment {
    id: string;
    content: string;
    userId: string;
    createdAt: string;
  }
  
  const [comments, setComments] = useState<Comment[]>([]);

  useEffect(() => {
    const fetchComments = async () => {
      const data = await getAllCommentsForMovie(movieId);
      setComments(data);
    };
    fetchComments();
  }, [movieId]);

  return (
    <div>
      <h2>Comments</h2>
      <ul>
        {comments.map((comment) => (
          <li key={comment.id}>
            <p>{comment.content}</p>
            <small>By User {comment.userId} on {new Date(comment.createdAt).toLocaleDateString()}</small>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MovieComments;
