import React, { useEffect, useState } from 'react';
import { getNowPlayingMovies } from '../api/movieApi';

const NowPlayingMovies = () => {
  interface Movie {
    id: number;
    title: string;
    overview: string;
    fullPosterPath: string;
  }

  const [movies, setMovies] = useState<Movie[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true); // Track if more movies are available

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true);
        const data = await getNowPlayingMovies(currentPage);
        console.log('Fetched movies:', data);

        // Append new movies to the existing list
        setMovies((prevMovies) => [...prevMovies, ...(data.movies || [])]);

        // Check if there are more movies to fetch
        if (!data.movies || data.movies.length === 0) {
          setHasMore(false);
        }
      } catch (error) {
        console.error('Error fetching movies:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, [currentPage]);

  // Infinite scroll handler
  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + document.documentElement.scrollTop >=
          document.documentElement.offsetHeight - 100 &&
        !loading &&
        hasMore
      ) {
        setCurrentPage((prevPage) => prevPage + 1);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading, hasMore]);

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">Now Playing Movies</h2>

      <div className="row">
        {movies.map((movie) => (
          <div className="col-md-4 mb-4" key={movie.id}>
            <div className="card">
              <img
                src={movie.fullPosterPath}
                className="card-img-top"
                alt={movie.title}
              />
              <div className="card-body">
                <h5 className="card-title">{movie.title}</h5>
                <p className="card-text">{movie.overview}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {loading && (
        <div className="d-flex justify-content-center mt-4">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}

      {!hasMore && (
        <p className="text-center mt-4">No more movies to load.</p>
      )}
    </div>
  );
};

export default NowPlayingMovies;
