import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { getAllGenres, searchMovies } from '../api/movieApi';

const Header: React.FC = () => {
  const location = useLocation(); // Use location from the parent Router
  const [genres, setGenres] = useState<{ id: number; name: string }[]>([]); // List of genres
  const [selectedGenres, setSelectedGenres] = useState<string[]>([]); // Selected genres
  const [showDropdown, setShowDropdown] = useState(false); // Toggle dropdown visibility
  const [searchQuery, setSearchQuery] = useState(''); // Search query
  const [searchResults, setSearchResults] = useState<any[]>([]); // Search results

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const data = await getAllGenres();
        setGenres(data);
      } catch (error) {
        console.error('Error fetching genres:', error);
      }
    };

    fetchGenres();
  }, []);

  const handleGenreSelect = (genre: string) => {
    if (!selectedGenres.includes(genre)) {
      setSelectedGenres((prev) => [...prev, genre]);
    }
    setShowDropdown(false);
  };

  const handleRemoveGenre = (genre: string) => {
    setSelectedGenres((prev) => prev.filter((g) => g !== genre));
  };

  const handleSearch = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const results = await searchMovies(searchQuery);
      setSearchResults(results);
      console.log('Search Results:', results); // Debugging: Log search results
    } catch (error) {
      console.error('Error searching movies:', error);
    }
  };

  if (location.pathname !== '/') {
    return null;
  }

  return (
    <div className="header-container p-3 bg-light">
      {/* Search Bar */}
      <form onSubmit={handleSearch} className="mb-3">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search movies..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button type="submit" className="btn btn-primary">
            Search
          </button>
        </div>
      </form>

      {/* Genre Dropdown */}
      <div className="genre-input-container position-relative">
        <input
          type="text"
          className="form-control"
          placeholder="Select genres"
          onClick={() => setShowDropdown((prev) => !prev)}
          readOnly
          value={selectedGenres.join(', ')}
        />
        {showDropdown && (
          <ul className="dropdown-menu show w-100">
            {genres.map((genre) => (
              <li key={genre.id} onClick={() => handleGenreSelect(genre.name)} className="dropdown-item">
                {genre.name}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Selected Genres */}
      <div className="selected-genres mt-2">
        {selectedGenres.map((genre) => (
          <span key={genre} className="badge bg-primary me-2">
            {genre}
            <button
              type="button"
              className="btn-close btn-close-white ms-1"
              aria-label="Remove"
              onClick={() => handleRemoveGenre(genre)}
            ></button>
          </span>
        ))}
      </div>
    </div>
  );
};

export default Header;
