import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { getAllGenres } from '../api/movieApi';

const Header: React.FC = () => {
  const location = useLocation(); // Use location from the parent Router
  const [genres, setGenres] = useState<{ id: number; name: string }[]>([]); // Updated type for genres
  const [selectedGenres, setSelectedGenres] = useState<string[]>([]);
  const [showDropdown, setShowDropdown] = useState(false);

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const data = await getAllGenres();
        setGenres(data);
      } catch (error) {
        console.error('Error fetching genres:', error);
      }
    };

    fetchGenres();
  }, []);

  const handleGenreSelect = (genre: string) => {
    if (!selectedGenres.includes(genre)) {
      setSelectedGenres((prev) => [...prev, genre]);
    }
    setShowDropdown(false);
  };

  const handleRemoveGenre = (genre: string) => {
    setSelectedGenres((prev) => prev.filter((g) => g !== genre));
  };

  if (location.pathname !== '/') {
    return null;
  }

  return (
    <div className="header-container p-3 bg-light">
      <div className="genre-input-container position-relative">
        <input
          type="text"
          className="form-control"
          placeholder="Select genres"
          onClick={() => setShowDropdown((prev) => !prev)}
          readOnly
          value={selectedGenres.join(', ')}
        />
        {showDropdown && (
          <ul className="dropdown-menu show w-100">
            {genres.map((genre) => (
              <li key={genre.id} onClick={() => handleGenreSelect(genre.name)} className="dropdown-item">
                {genre.name}
              </li>
            ))}
          </ul>
        )}
      </div>
      <div className="selected-genres mt-2">
        {selectedGenres.map((genre) => (
          <span key={genre} className="badge bg-primary me-2">
            {genre}
            <button
              type="button"
              className="btn-close btn-close-white ms-1"
              aria-label="Remove"
              onClick={() => handleRemoveGenre(genre)}
            ></button>
          </span>
        ))}
      </div>
    </div>
  );
};

export default Header;
