import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import { getNowPlayingMovies, getTopRatedMovies } from '../api/movieApi';
import Header from './Header';

const NowPlayingMovies = () => {
    const [movies, setMovies] = useState<any[]>([]);
    const [loading, setLoading] = useState(false);
    const [hasMore, setHasMore] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
  
    const handleSearchResults = (results: any[], append: boolean) => {
      setMovies((prevMovies) => (append ? [...prevMovies, ...results] : results));
      setHasMore(results.length > 0); // Check if more results are available
    };
  
    const handleNextPage = (page: number) => {
      setCurrentPage(page);
    };
  
    // Infinite scroll handler
    useEffect(() => {
      const handleScroll = () => {
        if (
          window.innerHeight + document.documentElement.scrollTop >=
            document.documentElement.offsetHeight - 100 &&
          !loading &&
          hasMore
        ) {
          handleNextPage(currentPage + 1); // Trigger the next page
        }
      };
  
      window.addEventListener('scroll', handleScroll);
      return () => window.removeEventListener('scroll', handleScroll);
    }, [loading, hasMore, currentPage]);
  
    return (
      <div className="container mt-4">
        <Header
          onSearchResults={handleSearchResults}
          onNextPage={handleNextPage}
        />
        <div className="row">
          {movies.map((movie) => (
            <div className="col-md-4 mb-4" key={movie.id}>
              <div className="card">
                <img
                  src={movie.fullPosterPath}
                  className="card-img-top"
                  alt={movie.title}
                />
                <div className="card-body">
                  <h5 className="card-title">{movie.title}</h5>
                  <p className="card-text">{movie.overview}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
        {loading && (
          <div className="d-flex justify-content-center mt-4">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        )}
      </div>
    );
  };
  
  export default NowPlayingMovies;
