import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import { getNowPlayingMovies, getTopRatedMovies } from '../api/movieApi';

const NowPlayingMovies = () => {
  interface Movie {
    id: number;
    title: string;
    overview: string;
    fullPosterPath: string;
  }

  const [movies, setMovies] = useState<Movie[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true); // Track if more movies are available
  const [category, setCategory] = useState<'current' | 'topRated'>('current'); // Track selected category

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true);
        let data: Movie[]; // The API returns only a list of movies
        if (category === 'current') {
          data = await getNowPlayingMovies(currentPage);
        } else {
          data = await getTopRatedMovies(currentPage);
        }
        console.log('Fetched movies:', data);

        // Append new movies to the existing list
        setMovies((prevMovies) => [...prevMovies, ...data]);

        // Check if there are more movies to fetch
        if (data.length === 0) {
          setHasMore(false);
        }
      } catch (error) {
        console.error('Error fetching movies:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, [currentPage, category]);

  // Infinite scroll handler
  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + document.documentElement.scrollTop >=
          document.documentElement.offsetHeight - 100 &&
        !loading &&
        hasMore
      ) {
        setCurrentPage((prevPage) => prevPage + 1);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading, hasMore]);

  const handleCategoryChange = (newCategory: 'current' | 'topRated') => {
    setCategory(newCategory);
    setMovies([]); // Clear the movie list
    setCurrentPage(1); // Reset to the first page
    setHasMore(true); // Reset the `hasMore` state
  };

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">Movies</h2>

      {/* Buttons for switching categories */}
      <div className="d-flex justify-content-center mb-4">
        <button
          className={`btn btn-primary me-2 ${category === 'current' ? 'active' : ''}`}
          onClick={() => handleCategoryChange('current')}
        >
          Current Movies
        </button>
        <button
          className={`btn btn-primary ${category === 'topRated' ? 'active' : ''}`}
          onClick={() => handleCategoryChange('topRated')}
        >
          Top Rated Movies
        </button>
      </div>

      <div className="row">
        {movies.map((movie) => (
          <div className="col-md-4 mb-4" key={movie.id}>
            <Link to={`/movie/${movie.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <div className="card">
                <img
                  src={movie.fullPosterPath}
                  className="card-img-top"
                  alt={movie.title}
                />
                <div className="card-body">
                  <h5 className="card-title">{movie.title}</h5>
                  <p className="card-text">{movie.overview}</p>
                </div>
              </div>
            </Link>
          </div>
        ))}
      </div>

      {loading && (
        <div className="d-flex justify-content-center mt-4">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}

      {!hasMore && (
        <p className="text-center mt-4">No more movies to load.</p>
      )}
    </div>
  );
};

export default NowPlayingMovies;
