import React, { useEffect, useState, useContext } from 'react';
import { getAllCommentsForMovie } from '../api/commentApi';
import AddComment from './AddComment';
import { AuthContext } from '../context/AuthContext';

interface MovieCommentsProps {
  movieId: string;
}

const MovieComments = ({ movieId }: MovieCommentsProps) => {
  interface Comment {
    id: string;
    content: string;
    username: string; // Changed from userId to username
    createdAt: string;
  }

  const [comments, setComments] = useState<Comment[]>([]);
  const { user } = useContext(AuthContext); // Access user from AuthContext

  const refreshComments = async () => {
    const data = await getAllCommentsForMovie(movieId);
    setComments(data);
  };

  useEffect(() => {
    refreshComments();
  }, [movieId]);

  return (
    <div style={{ padding: '1rem', maxWidth: '600px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '1rem' }}>Comments</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {comments.map((comment) => (
          <li
            key={comment.id}
            style={{
              border: '1px solid #ddd',
              borderRadius: '8px',
              padding: '1rem',
              marginBottom: '1rem',
              backgroundColor: '#f9f9f9',
            }}
          >
            <p
              style={{
                marginBottom: '0.5rem',
                fontSize: '1rem',
                lineHeight: '1.5',
              }}
            >
              {comment.content}
            </p>
            <small
              style={{
                display: 'block',
                color: '#555',
                fontSize: '0.875rem',
              }}
            >
              By <strong>{comment.username}</strong> on{' '}
              {new Date(comment.createdAt).toLocaleDateString()}
            </small>
          </li>
        ))}
      </ul>
      {user && <AddComment movieId={movieId} onCommentAdded={refreshComments} />}
    </div>
  );
};

export default MovieComments;
