import React, { createContext, useState, useEffect } from 'react';
import { login, refreshToken } from '../api/authApi';

export const AuthContext = createContext(); // Ensure this is exported

const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [refreshTokenValue, setRefreshTokenValue] = useState(null);

  const handleLogin = async (email, password) => {
    const data = await login(email, password);
    setAccessToken(data.accessToken);
    setRefreshTokenValue(data.refreshToken);
    setUser({ email });
    localStorage.setItem('refreshToken', data.refreshToken);
  };

  const handleLogout = () => {
    setUser(null);
    setAccessToken(null);
    setRefreshTokenValue(null);
    localStorage.removeItem('refreshToken');
  };

  const handleRefreshToken = async () => {
    const storedRefreshToken = localStorage.getItem('refreshToken');
    if (!storedRefreshToken) return;

    try {
      const data = await refreshToken(storedRefreshToken);
      setAccessToken(data.accessToken);
      setRefreshTokenValue(data.refreshToken);
      localStorage.setItem('refreshToken', data.refreshToken);
    } catch (error) {
      handleLogout();
    }
  };

  useEffect(() => {
    handleRefreshToken();
  }, []);

  return (
    <AuthContext.Provider value={{ user, accessToken, handleLogin, handleLogout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
