import React, { useEffect, useState, useContext } from 'react';
import { getAllCommentsForMovie } from '../api/commentApi';
import AddComment from './AddComment';
import { AuthContext } from '../context/AuthContext';

interface MovieCommentsProps {
  movieId: string;
}

const MovieComments = ({ movieId }: MovieCommentsProps) => {
  interface Comment {
    id: string;
    content: string;
    userId: string;
    createdAt: string;
  }

  const [comments, setComments] = useState<Comment[]>([]);
  const { user } = useContext(AuthContext); // Access user from AuthContext

  const refreshComments = async () => {
    const data = await getAllCommentsForMovie(movieId);
    setComments(data);
  };

  useEffect(() => {
    refreshComments();
  }, [movieId]);

  return (
    <div>
      <h2>Comments</h2>
      <ul>
        {comments.map((comment) => (
          <li key={comment.id}>
            <p>{comment.content}</p>
            <small>
              By User {comment.userId} on{' '}
              {new Date(comment.createdAt).toLocaleDateString()}
            </small>
          </li>
        ))}
      </ul>
      {user && <AddComment movieId={movieId} onCommentAdded={refreshComments} />}
    </div>
  );
};

export default MovieComments;
