import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { searchMovies } from '../api/movieApi';
import Header from './Header';

const MoviesList: React.FC = () => {
  interface Movie {
    id: number;
    title: string;
    overview: string;
    poster_path: string;
  }

  const [movies, setMovies] = useState<Movie[]>([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [query, setQuery] = useState('');
  const [genres, setGenres] = useState<string[]>([]);

  const fetchMovies = async (reset = false) => {
    try {
      setLoading(true);
      const data = await searchMovies(query, page);
      setMovies((prev) => (reset ? data : [...prev, ...data]));
      setHasMore(data.length > 0);
    } catch (error) {
      console.error('Error fetching movies:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, [page]);

  const handleSearch = (searchQuery: string, selectedGenres: string[]) => {
    setQuery(searchQuery);
    setGenres(selectedGenres);
    setPage(1);
    fetchMovies(true); // Reset the movie list
  };

  const loadMore = () => {
    if (!loading && hasMore) {
      setPage((prev) => prev + 1);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + document.documentElement.scrollTop >=
          document.documentElement.offsetHeight - 100 &&
        hasMore
      ) {
        loadMore();
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [hasMore, loading]);

  return (
    <div>
      <Header onSearch={handleSearch} />
      <div className="container mt-4">
        <h2 className="text-center mb-4">Movies</h2>
        <div className="row">
          {movies.map((movie) => (
            <div className="col-md-3 mb-4" key={movie.id}>
              <Link to={`/movie/${movie.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                <div className="card">
                  <img
                    src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                    className="card-img-top"
                    alt={movie.title}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                    <p className="card-text">{movie.overview}</p>
                  </div>
                </div>
              </Link>
            </div>
          ))}
        </div>
        {loading && (
          <div className="d-flex justify-content-center mt-4">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        )}
        {!hasMore && !loading && movies.length === 0 && (
          <p className="text-center mt-4">No movies found.</p>
        )}
      </div>
    </div>
  );
};

export default MoviesList;
