import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { getAllGenres, searchMovies } from '../api/movieApi';

interface HeaderProps {
    onSearchResults: (results: any[], append: boolean) => void; // Callback to update movie list
    onNextPage: (page: number) => void; // Callback to fetch the next page
  }
  
  const Header: React.FC<HeaderProps> = ({ onSearchResults, onNextPage }) => {
    const location = useLocation();
    const [genres, setGenres] = useState<{ id: number; name: string }[]>([]);
    const [selectedGenres, setSelectedGenres] = useState<string[]>([]);
    const [showDropdown, setShowDropdown] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
  
    useEffect(() => {
      const fetchGenres = async () => {
        try {
          const data = await getAllGenres();
          setGenres(data);
        } catch (error) {
          console.error('Error fetching genres:', error);
        }
      };
  
      fetchGenres();
    }, []);
  
    const handleGenreSelect = (genre: string) => {
      if (!selectedGenres.includes(genre)) {
        setSelectedGenres((prev) => [...prev, genre]);
      }
      setShowDropdown(false);
    };
  
    const handleRemoveGenre = (genre: string) => {
      setSelectedGenres((prev) => prev.filter((g) => g !== genre));
    };
  
    const fetchSearchResults = async (page: number, append: boolean) => {
      try {
        const response = await searchMovies(searchQuery, page);
        onSearchResults(response.results, append); // Pass results to MoviesList
      } catch (error) {
        console.error('Error fetching search results:', error);
      }
    };
  
    const handleSearch = async (e: React.FormEvent) => {
      e.preventDefault();
      setCurrentPage(1); // Reset to the first page
      await fetchSearchResults(1, false); // Fetch results for the first page
    };
  
    // Triggered by MoviesList to fetch the next page
    useEffect(() => {
      if (currentPage > 1) {
        fetchSearchResults(currentPage, true); // Fetch next page and append results
      }
    }, [currentPage]);
  
    if (location.pathname !== '/') {
      return null;
    }
  
    return (
      <div className="header-container p-3 bg-light">
        <form onSubmit={handleSearch} className="row g-3 align-items-center">
          {/* Search Bar */}
          <div className="col-md-6">
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                placeholder="Search movies..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
          </div>
  
          {/* Genre Dropdown with Badges Inside Input */}
          <div className="col-md-6 position-relative">
            <div
              className="form-control d-flex align-items-center flex-wrap"
              style={{ cursor: 'pointer' }}
              onClick={() => setShowDropdown((prev) => !prev)}
            >
              {selectedGenres.map((genre) => (
                <span key={genre} className="badge bg-primary me-2 mb-1">
                  {genre}
                  <button
                    type="button"
                    className="btn-close btn-close-white ms-1"
                    aria-label="Remove"
                    onClick={(e) => {
                      e.stopPropagation(); // Prevent dropdown toggle
                      handleRemoveGenre(genre);
                    }}
                  ></button>
                </span>
              ))}
              {selectedGenres.length === 0 && (
                <span className="text-muted">Select genres</span>
              )}
            </div>
            {showDropdown && (
              <ul
                className="dropdown-menu show w-100"
                style={{
                  position: 'absolute',
                  top: '100%',
                  left: 0,
                  zIndex: 1050,
                }}
              >
                {genres.map((genre) => (
                  <li
                    key={genre.id}
                    onClick={() => handleGenreSelect(genre.name)}
                    className="dropdown-item"
                  >
                    {genre.name}
                  </li>
                ))}
              </ul>
            )}
          </div>
  
          {/* Search Button */}
          <div className="col-12 text-center">
            <button type="submit" className="btn btn-primary">
              Search
            </button>
          </div>
        </form>
      </div>
    );
  };
  
  export default Header;
