import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { getAllGenres, searchMovies } from '../api/movieApi';

interface HeaderProps {
  onSearchResults: (results: any[]) => void; // Callback to update movie list
}

const Header: React.FC<HeaderProps> = ({ onSearchResults }) => {
  const location = useLocation();
  const [genres, setGenres] = useState<{ id: number; name: string }[]>([]);
  const [selectedGenres, setSelectedGenres] = useState<string[]>([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const data = await getAllGenres();
        setGenres(data);
      } catch (error) {
        console.error('Error fetching genres:', error);
      }
    };

    fetchGenres();
  }, []);

  const handleGenreSelect = (genre: string) => {
    if (!selectedGenres.includes(genre)) {
      setSelectedGenres((prev) => [...prev, genre]);
    }
    setShowDropdown(false);
  };

  const handleRemoveGenre = (genre: string) => {
    setSelectedGenres((prev) => prev.filter((g) => g !== genre));
  };

  const handleSearch = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const results = await searchMovies(searchQuery);
      onSearchResults(results); // Pass search results to MoviesList
    } catch (error) {
      console.error('Error searching movies:', error);
    }
  };

  if (location.pathname !== '/') {
    return null;
  }

  return (
    <div className="header-container p-3 bg-light">
      <form onSubmit={handleSearch} className="row g-3 align-items-center">
        {/* Search Bar */}
        <div className="col-md-6">
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              placeholder="Search movies..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>

        {/* Genre Dropdown with Badges Inside Input */}
        <div className="col-md-6">
          <div
            className="form-control d-flex align-items-center flex-wrap position-relative"
            style={{ cursor: 'pointer' }}
            onClick={() => setShowDropdown((prev) => !prev)}
          >
            {selectedGenres.map((genre) => (
              <span key={genre} className="badge bg-primary me-2 mb-1">
                {genre}
                <button
                  type="button"
                  className="btn-close btn-close-white ms-1"
                  aria-label="Remove"
                  onClick={(e) => {
                    e.stopPropagation(); // Prevent dropdown toggle
                    handleRemoveGenre(genre);
                  }}
                ></button>
              </span>
            ))}
            {selectedGenres.length === 0 && (
              <span className="text-muted">Select genres</span>
            )}
          </div>
          {showDropdown && (
            <ul className="dropdown-menu show w-100">
              {genres.map((genre) => (
                <li
                  key={genre.id}
                  onClick={() => handleGenreSelect(genre.name)}
                  className="dropdown-item"
                >
                  {genre.name}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Search Button */}
        <div className="col-12 text-center">
          <button type="submit" className="btn btn-primary">
            Search
          </button>
        </div>
      </form>
    </div>
  );
};

export default Header;
