import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getNowPlayingMovies } from '../api/movieApi';

const MoviesList: React.FC = () => {
  interface Movie {
    id: number;
    title: string;
    overview: string;
    poster_path: string;
  }

  const [movies, setMovies] = useState<Movie[]>([]);
  const [loading, setLoading] = useState(false);

  // Fetch the latest movies by default
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true);
        const data = await getNowPlayingMovies();
        setMovies(data);
      } catch (error) {
        console.error('Error fetching movies:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  const handleSearchResults = (results: Movie[]) => {
    setMovies(results); // Update the movie list with search results
  };

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">Latest Movies</h2>
      {loading && (
        <div className="d-flex justify-content-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}
      <div className="row">
        {movies.map((movie) => (
          <div className="col-md-3 mb-4" key={movie.id}>
            <Link to={`/movie/${movie.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <div className="card">
                <img
                  src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                  className="card-img-top"
                  alt={movie.title}
                />
                <div className="card-body">
                  <h5 className="card-title">{movie.title}</h5>
                  <p className="card-text">{movie.overview}</p>
                </div>
              </div>
            </Link>
          </div>
        ))}
      </div>
      {!loading && movies.length === 0 && (
        <p className="text-center mt-4">No movies found.</p>
      )}
    </div>
  );
};

export default MoviesList;
