import React, { useEffect, useState } from 'react';
import { getNowPlayingMovies } from '../api/movieApi';

const NowPlayingMovies = () => {
  interface Movie {
    id: number;
    title: string;
    overview: string;
    fullPosterPath: string;
  }

  const [movies, setMovies] = useState<Movie[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true); // Set loading to true before fetching
        const data = await getNowPlayingMovies(currentPage); // Pass currentPage to the API
        setMovies(data.movies || []); // Ensure movies is an array
        setTotalPages(data.totalPages || 1); // Ensure totalPages is valid
      } catch (error) {
        console.error('Error fetching movies:', error);
      } finally {
        setLoading(false); // Set loading to false after fetching
      }
    };
    fetchMovies();
  }, [currentPage]);

  // Change page
  const paginate = (pageNumber: number) => setCurrentPage(pageNumber);

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">Now Playing Movies</h2>

      {loading ? (
        <p className="text-center">Loading...</p> // Show loading message
      ) : (
        <>
          <div className="row">
            {movies.map((movie) => (
              <div className="col-md-4 mb-4" key={movie.id}>
                <div className="card">
                  <img
                    src={movie.fullPosterPath}
                    className="card-img-top"
                    alt={movie.title}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                    <p className="card-text">{movie.overview}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Pagination */}
          <nav>
            <ul className="pagination justify-content-center">
              {Array.from({ length: totalPages }, (_, i) => i + 1).map((number) => (
                <li
                  key={number}
                  className={`page-item ${number === currentPage ? 'active' : ''}`}
                >
                  <button
                    className="page-link"
                    onClick={() => paginate(number)}
                  >
                    {number}
                  </button>
                </li>
              ))}
            </ul>
          </nav>
        </>
      )}
    </div>
  );
};

export default NowPlayingMovies;
