import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { getAllGenres, searchMovies } from '../api/movieApi';

interface HeaderProps {
    onSearchResults: (query: string, genres: string[]) => void; // Pass genres to parent
  }
  
  const Header: React.FC<HeaderProps> = ({ onSearchResults }) => {
    const [genres, setGenres] = useState<{ id: number; name: string }[]>([]);
    const [selectedGenres, setSelectedGenres] = useState<string[]>([]);
    const [searchQuery, setSearchQuery] = useState('');
  
    useEffect(() => {
      const fetchGenres = async () => {
        try {
          const data = await getAllGenres();
          setGenres(data);
        } catch (error) {
          console.error('Error fetching genres:', error);
        }
      };
  
      fetchGenres();
    }, []);
  
    const handleGenreSelect = (genre: string) => {
      if (!selectedGenres.includes(genre)) {
        setSelectedGenres((prev) => [...prev, genre]);
      }
    };
  
    const handleRemoveGenre = (genre: string) => {
      setSelectedGenres((prev) => prev.filter((g) => g !== genre));
    };
  
    const handleSearch = (e: React.FormEvent) => {
      e.preventDefault();
      onSearchResults(searchQuery, selectedGenres); // Pass query and genres to parent
    };
  
    return (
      <div className="header-container p-3 bg-light">
        <form onSubmit={handleSearch} className="row g-3 align-items-center">
          {/* Search Bar */}
          <div className="col-md-6">
            <input
              type="text"
              className="form-control"
              placeholder="Search movies..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
  
          {/* Genre Dropdown */}
          <div className="col-md-6 position-relative">
            <div
              className="form-control d-flex align-items-center flex-wrap"
              style={{ cursor: 'pointer' }}
            >
              {selectedGenres.map((genre) => (
                <span key={genre} className="badge bg-primary me-2 mb-1">
                  {genre}
                  <button
                    type="button"
                    className="btn-close btn-close-white ms-1"
                    aria-label="Remove"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleRemoveGenre(genre);
                    }}
                  ></button>
                </span>
              ))}
              {selectedGenres.length === 0 && <span className="text-muted">Select genres</span>}
            </div>
            <ul className="dropdown-menu show w-100">
              {genres.map((genre) => (
                <li
                  key={genre.id}
                  onClick={() => handleGenreSelect(genre.name)}
                  className="dropdown-item"
                >
                  {genre.name}
                </li>
              ))}
            </ul>
          </div>
  
          {/* Search Button */}
          <div className="col-12 text-center">
            <button type="submit" className="btn btn-primary">
              Search
            </button>
          </div>
        </form>
      </div>
    );
  };
  
  export default Header;
