import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMovieDetails } from '../api/movieApi';
import MovieComments from './MovieComments';
import AddComment from './AddComment';

const MovieDetails: React.FC = () => {
  const { movieId } = useParams<{ movieId: string }>();
  const [movieDetails, setMovieDetails] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showFullCast, setShowFullCast] = useState(false); // State to toggle cast visibility
  const [showFullCrew, setShowFullCrew] = useState(false); // State to toggle crew visibility

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        if (movieId) {
          const data = await getMovieDetails(movieId);
          setMovieDetails(data);
        }
      } catch (err) {
        setError('Failed to fetch movie details.');
      } finally {
        setLoading(false);
      }
    };

    fetchDetails();
  }, [movieId]);

  if (loading) return <p className="text-center mt-4">Loading...</p>;
  if (error) return <p className="text-center text-danger mt-4">{error}</p>;

  const toggleCastVisibility = () => {
    setShowFullCast((prev) => !prev);
  };

  const toggleCrewVisibility = () => {
    setShowFullCrew((prev) => !prev);
  };

  const displayedCast = showFullCast
    ? movieDetails.credits.cast
    : movieDetails.credits.cast.slice(0, 5); // Show only 5 cast members by default

  const displayedCrew = showFullCrew
    ? movieDetails.credits.crew
    : movieDetails.credits.crew.slice(0, 5); // Show only 5 crew members by default

  return (
    <div className="container mt-4">
      <div className="row">
        {/* Movie Poster */}
        <div className="col-md-4">
          <img
            src={`https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`}
            alt={movieDetails.title}
            className="img-fluid rounded shadow"
          />
        </div>

        {/* Movie Details */}
        <div className="col-md-8">
          <h1 className="mb-3">{movieDetails.title}</h1>
          <p className="text-muted">{movieDetails.overview}</p>
          <p>
            <strong>Release Date:</strong> {movieDetails.release_date}
          </p>
          <p>
            <strong>Genres:</strong> {movieDetails.genres.map((g: any) => g.name).join(', ')}
          </p>
               {/* Cast and Crew Section */}
      <div className="row mt-5">
        {/* Cast Section */}
        <div className="col-md-4">
          <h2>Cast</h2>
          <ul className="list-unstyled">
            {displayedCast.map((cast: any) => (
              <li key={cast.castId} className="mb-2">
                <strong>{cast.name}</strong> as {cast.character}
              </li>
            ))}
          </ul>
          {movieDetails.credits.cast.length > 5 && (
            <button
              className="btn btn-link p-0"
              onClick={toggleCastVisibility}
            >
              {showFullCast ? 'See Less' : 'See More'}
            </button>
          )}
        </div>

        {/* Crew Section */}
        <div className="col-md-4">
          <h2>Crew</h2>
          <ul className="list-unstyled">
            {displayedCrew.map((crew: any) => (
              <li key={crew.name} className="mb-2">
                <strong>{crew.name}</strong> - {crew.job}
              </li>
            ))}
          </ul>
          {movieDetails.credits.crew.length > 5 && (
            <button
              className="btn btn-link p-0"
              onClick={toggleCrewVisibility}
            >
              {showFullCrew ? 'See Less' : 'See More'}
            </button>
          )}
        </div>
      </div>
        </div>
      </div>

 

      {/* Images Section */}
      <div className="mt-5">
        <h2>Images</h2>
        <div className="d-flex flex-wrap">
          {movieDetails.images.backdrops.map((image: any, index: number) => (
            <img
              key={index}
              src={`https://image.tmdb.org/t/p/w500${image.file_path}`}
              alt="Backdrop"
              className="img-thumbnail m-2"
              style={{ width: '200px' }}
            />
          ))}
        </div>
      </div>

      {/* Comments Section */}
      <div className="mt-5">
        <h2>Comments</h2>
        {movieId && <MovieComments movieId={movieId} />}
        {movieId && <AddComment movieId={movieId} />}
      </div>
    </div>
  );
};

export default MovieDetails;
