import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMovieDetails } from '../api/movieApi';
import MovieComments from './MovieComments';
import AddComment from './AddComment';

const MovieDetails: React.FC = () => {
    const { movieId } = useParams<{ movieId: string }>();
    const [movieDetails, setMovieDetails] = useState<any>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);
    const [selectedImageIndex, setSelectedImageIndex] = useState<number | null>(null); // Track selected image index
    const [showAllImages, setShowAllImages] = useState(false); // State to toggle image visibility

    useEffect(() => {
        const fetchDetails = async () => {
            try {
                if (movieId) {
                    const data = await getMovieDetails(movieId);
                    setMovieDetails(data);
                }
            } catch (err) {
                setError('Failed to fetch movie details.');
            } finally {
                setLoading(false);
            }
        };

        fetchDetails();
    }, [movieId]);

    if (loading) return <p className="text-center mt-4">Loading...</p>;
    if (error) return <p className="text-center text-danger mt-4">{error}</p>;

    const displayedImages = showAllImages
        ? movieDetails.images.backdrops
        : movieDetails.images.backdrops.slice(0, 5); // Show only 5 images by default

    const openImageModal = (index: number) => {
        setSelectedImageIndex(index);
    };

    const closeImageModal = () => {
        setSelectedImageIndex(null);
    };

    const showNextImage = () => {
        if (selectedImageIndex !== null && selectedImageIndex < movieDetails.images.backdrops.length - 1) {
            setSelectedImageIndex(selectedImageIndex + 1);
        }
    };

    const showPreviousImage = () => {
        if (selectedImageIndex !== null && selectedImageIndex > 0) {
            setSelectedImageIndex(selectedImageIndex - 1);
        }
    };

    return (
        <div className="container mt-4">
            <div className="row">
                {/* Movie Poster */}
                <div className="col-md-4">
                    <img
                        src={`https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`}
                        alt={movieDetails.title}
                        className="img-fluid rounded shadow"
                    />
                </div>

                {/* Movie Details */}
                <div className="col-md-8">
                    <h1 className="mb-3">{movieDetails.title}</h1>
                    <p className="text-muted">{movieDetails.overview}</p>
                    <p>
                        <strong>Release Date:</strong> {movieDetails.release_date}
                    </p>
                    <p>
                        <strong>Genres:</strong> {movieDetails.genres.map((g: any) => g.name).join(', ')}
                    </p>
                </div>
            </div>

            {/* Images Section */}
            <div className="mt-5">
                <h2>Images</h2>
                <div className="d-flex flex-wrap">
                    {displayedImages.map((image: any, index: number) => (
                        <img
                            key={index}
                            src={`https://image.tmdb.org/t/p/w500${image.file_path}`}
                            alt="Backdrop"
                            className="img-thumbnail m-2"
                            style={{ width: '200px', cursor: 'pointer' }}
                            onClick={() => openImageModal(index)} // Open modal on click
                        />
                    ))}
                </div>
                {movieDetails.images.backdrops.length > 5 && (
                    <button
                        className="btn btn-link p-0 mt-2"
                        onClick={() => setShowAllImages((prev) => !prev)}
                    >
                        {showAllImages ? 'See Less' : 'See More'}
                    </button>
                )}
            </div>

            {/* Image Modal */}
            {selectedImageIndex !== null && (
                <div
                    className="modal fade show d-block"
                    tabIndex={-1}
                    role="dialog"
                    style={{ backgroundColor: 'rgba(0, 0, 0, 0.8)' }}
                >
                    <div className="modal-dialog modal-dialog-centered modal-xl" role="document">
                        <div className="modal-content bg-dark text-white">
                            <div className="modal-header border-0">
                                <button
                                    type="button"
                                    className="btn-close btn-close-white"
                                    aria-label="Close"
                                    onClick={closeImageModal}
                                ></button>
                            </div>
                            <div className="modal-body text-center position-relative">
                                {/* Left Arrow */}
                                {selectedImageIndex > 0 && (
                                    <button
                                        className="btn btn-dark position-absolute top-50 start-0 translate-middle-y"
                                        style={{ zIndex: 1050 }}
                                        onClick={showPreviousImage}
                                    >
                                        <i className="bi bi-arrow-left-circle fs-4"></i>
                                    </button>
                                )}

                                {/* Image */}
                                <img
                                    src={`https://image.tmdb.org/t/p/original${movieDetails.images.backdrops[selectedImageIndex].file_path}`}
                                    alt="Selected"
                                    className="img-fluid"
                                    style={{
                                        maxHeight: '80vh',
                                        maxWidth: '100%',
                                        objectFit: 'contain',
                                    }}
                                />

                                {/* Right Arrow */}
                                {selectedImageIndex < movieDetails.images.backdrops.length - 1 && (
                                    <button
                                        className="btn btn-dark position-absolute top-50 end-0 translate-middle-y"
                                        style={{ zIndex: 1050 }}
                                        onClick={showNextImage}
                                    >
                                        <i className="bi bi-arrow-right-circle fs-4"></i>
                                    </button>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Comments Section */}
            <div className="mt-5">
                <h2>Comments</h2>
                {movieId && <MovieComments movieId={movieId} />}
                {movieId && <AddComment movieId={movieId} onCommentAdded={() => console.log('Comment added')} />}
            </div>
        </div>
    );
};

export default MovieDetails;
