import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMovieDetails } from '../api/movieApi';
import MovieComments from './MovieComments';
import AddComment from './AddComment';

const MovieDetails: React.FC = () => {
  const { movieId } = useParams<{ movieId: string }>();
  const [movieDetails, setMovieDetails] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        if (movieId) {
          const data = await getMovieDetails(movieId);
          setMovieDetails(data);
        }
      } catch (err) {
        setError('Failed to fetch movie details.');
      } finally {
        setLoading(false);
      }
    };

    fetchDetails();
  }, [movieId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="container mt-4">
      <h1>{movieDetails.title}</h1>
      <p>{movieDetails.overview}</p>
      <p>
        <strong>Release Date:</strong> {movieDetails.releaseDate}
      </p>
      <p>
        <strong>Genres:</strong> {movieDetails.genres.map((g: any) => g.name).join(', ')}
      </p>
      <img
        src={`https://image.tmdb.org/t/p/w500${movieDetails.posterPath}`}
        alt={movieDetails.title}
        className="img-fluid"
      />

      <h2>Cast</h2>
      <ul>
        {movieDetails.credits.cast.map((cast: any) => (
          <li key={cast.castId}>
            {cast.name} as {cast.character}
          </li>
        ))}
      </ul>

      <h2>Crew</h2>
      <ul>
        {movieDetails.credits.crew.map((crew: any) => (
          <li key={crew.name}>
            {crew.name} - {crew.job}
          </li>
        ))}
      </ul>

      <h2>Images</h2>
      <div>
        {movieDetails.images.backdrops.map((image: any, index: number) => (
          <img
            key={index}
            src={`https://image.tmdb.org/t/p/w500${image.filePath}`}
            alt="Backdrop"
            style={{ width: '200px', margin: '5px' }}
          />
        ))}
      </div>

      {/* Comments Section */}
      {movieId && <MovieComments movieId={movieId} />}
      {movieId && <AddComment movieId={movieId} />}
    </div>
  );
};

export default MovieDetails;
